(()=>{let startTime,updInterval=null,timer=document.getElementById("timer");function updateTimer(){var elapsedTime=Date.now()-startTime,seconds=Math.floor(elapsedTime/1e3%60),minutes=Math.floor(elapsedTime/1e3/60%60),hours=Math.floor(elapsedTime/1e3/60/60%24),days=Math.floor(elapsedTime/1e3/60/60/24%365),elapsedTime=[[Math.floor(elapsedTime/1e3/60/60/24/365),"y"],[days,"d"],[hours,"h"],[minutes,"m"],[seconds,"s"]],days=elapsedTime.findIndex(([v])=>0<v),hours=elapsedTime.slice(days).map(([v,l])=>""+(v+l)).join(" ");timer.textContent=hours}document.addEventListener("__unity_trigger_gamestart_event__",e=>{startTime=Date.now(),updInterval=setInterval(updateTimer,200)}),document.addEventListener("__unity_trigger_gameclear_event__",e=>{clearInterval(updInterval)})})();